    loadBalancerId: "nlb-11zz9w3jqptz449iegfwvxxxx",
    protocol: "TCP",
    port: 0,
    serverGroupId: "rsp-11zz9wdewa3uo49ieggq8xxxx",
    connectionTimeout: 60,
    description: "CLBNLBListenerDemo description",
    enabled: true,
    listenerName: "CLBNLBListenerDemo",
    tags: [{
        key: "env",
        value: "Test",
    }],
});
